    this.eventosService.getEventos('').subscribe(cs => {this.eventos = cs;});
  }

  ngOnInit() {
    // Agregado - Mock de prueba
    //this.canciones = [new Cancion("Prueba", 1, 10, "Memo")]
    this.getListaEventos();
  }

}
